import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { insertUserSchema, insertDoctorSchema, insertAppointmentSchema, insertHealthRecordSchema, insertSymptomCheckSchema } from "@shared/schema";
// EndlessMedical API - Free medical diagnosis API
// Documentation: https://www.endlessmedical.com/about-endlessmedical-api/

// EndlessMedical API Base URL
const ENDLESSMEDICAL_API_BASE = 'https://endlessmedical.com/v1/dx';

// Helper function to call EndlessMedical API
async function callEndlessMedicalAPI(endpoint: string, params: Record<string, string> = {}) {
  const url = new URL(`${ENDLESSMEDICAL_API_BASE}/${endpoint}`);
  Object.entries(params).forEach(([key, value]) => {
    url.searchParams.append(key, value);
  });

  const response = await fetch(url.toString());
  if (!response.ok) {
    throw new Error(`EndlessMedical API error: ${response.statusText}`);
  }
  return response.json();
}

// Analyze symptoms using EndlessMedical API
async function analyzeWithEndlessMedical(symptoms: string[]) {
  try {
    // Step 1: Initialize session
    const sessionResponse = await callEndlessMedicalAPI('InitSession');
    const sessionId = sessionResponse.SessionID;

    if (!sessionId) {
      throw new Error('Failed to initialize EndlessMedical session');
    }

    // Step 2: Set patient demographics (assuming adult patient)
    await callEndlessMedicalAPI('SetSexFemale', { SessionID: sessionId });
    await callEndlessMedicalAPI('UpdateFeature', { 
      SessionID: sessionId, 
      name: 'Age',
      value: '30' // Default age for rural healthcare
    });

    // Step 3: Add symptoms as features
    for (const symptom of symptoms) {
      // Clean symptom text and add as feature
      const cleanSymptom = symptom.trim().toLowerCase();
      await callEndlessMedicalAPI('UpdateFeature', { 
        SessionID: sessionId, 
        name: cleanSymptom,
        value: '1' // Present
      });
    }

    // Step 4: Get diagnosis
    const diagnosisResponse = await callEndlessMedicalAPI('Analyze', { SessionID: sessionId });

    // Step 5: Get top conditions
    const conditionsResponse = await callEndlessMedicalAPI('GetTopConditions', { 
      SessionID: sessionId,
      NumberOfResults: '5'
    });

    // Map EndlessMedical response to our format
    return mapEndlessMedicalResponse(diagnosisResponse, conditionsResponse, symptoms);
  } catch (error) {
    console.error('EndlessMedical API Error:', error);
    // Fallback to basic analysis if API fails
    return getFallbackAnalysis(symptoms);
  }
}

// Map EndlessMedical response to our expected format
function mapEndlessMedicalResponse(diagnosis: any, conditions: any, symptoms: string[]) {
  const topConditions = conditions.Conditions || [];
  const possibleConditions = topConditions.slice(0, 3).map((c: any) => c.Name || 'Unknown condition');

  // Determine severity based on conditions and symptoms
  const emergencyKeywords = ['chest pain', 'difficulty breathing', 'severe headache', 'unconscious', 'seizure'];
  const highKeywords = ['fever', 'severe pain', 'bleeding', 'shortness of breath'];
  const mediumKeywords = ['headache', 'nausea', 'dizzy', 'fatigue'];

  const symptomText = symptoms.join(' ').toLowerCase();
  let severity: 'low' | 'medium' | 'high' | 'emergency' = 'low';
  let urgency: 'routine' | 'urgent' | 'emergency' = 'routine';

  if (emergencyKeywords.some(keyword => symptomText.includes(keyword))) {
    severity = 'emergency';
    urgency = 'emergency';
  } else if (highKeywords.some(keyword => symptomText.includes(keyword))) {
    severity = 'high';
    urgency = 'urgent';
  } else if (mediumKeywords.some(keyword => symptomText.includes(keyword))) {
    severity = 'medium';
    urgency = 'routine';
  }

  const recommendations = generateRecommendations(severity, possibleConditions);

  return {
    possibleConditions: possibleConditions.length > 0 ? possibleConditions : ['General health assessment needed'],
    severity,
    recommendations,
    urgency,
    disclaimer: "This is a preliminary assessment powered by EndlessMedical AI. Always consult with a qualified healthcare professional for proper diagnosis and treatment. This tool is not a substitute for professional medical advice."
  };
}

// Generate recommendations based on severity and conditions
function generateRecommendations(severity: string, conditions: string[]) {
  const baseRecommendations = [
    "Monitor your symptoms closely",
    "Stay hydrated and get adequate rest",
    "Consult with a healthcare professional if symptoms persist"
  ];

  switch (severity) {
    case 'emergency':
      return [
        "Seek immediate medical attention",
        "Call emergency services (108 in India)",
        "Do not delay medical care",
        "Have someone accompany you to the hospital"
      ];
    case 'high':
      return [
        "Consult a doctor within 24 hours",
        "Monitor symptoms for any worsening",
        "Consider visiting a clinic or hospital",
        "Keep track of symptom changes"
      ];
    case 'medium':
      return [
        "Schedule an appointment with your doctor",
        "Continue monitoring symptoms",
        "Maintain good self-care practices",
        "Seek care if symptoms worsen"
      ];
    default:
      return baseRecommendations;
  }
}

// Fallback analysis when API is unavailable
function getFallbackAnalysis(symptoms: string[]) {
  const symptomText = symptoms.join(' ').toLowerCase();

  // Simple keyword-based analysis as fallback
  let severity: 'low' | 'medium' | 'high' | 'emergency' = 'medium';
  let urgency: 'routine' | 'urgent' | 'emergency' = 'routine';

  if (symptomText.includes('chest pain') || symptomText.includes('difficulty breathing') || symptomText.includes('severe')) {
    severity = 'emergency';
    urgency = 'emergency';
  } else if (symptomText.includes('fever') || symptomText.includes('pain')) {
    severity = 'high';
    urgency = 'urgent';
  }

  return {
    possibleConditions: ['Medical evaluation needed', 'Symptom assessment required'],
    severity,
    recommendations: generateRecommendations(severity, []),
    urgency,
    disclaimer: "This is a basic symptom assessment. Please consult with a qualified healthcare professional for proper diagnosis and treatment. This tool is not a substitute for professional medical advice."
  };
}

export async function registerRoutes(app: Express): Promise<Server> {
  const httpServer = createServer(app);

  // WebSocket server for video calls and real-time features
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  const rooms = new Map<string, Set<WebSocket>>();

  wss.on('connection', (ws: WebSocket) => {
    let currentRoom: string | null = null;

    ws.on('message', async (message) => {
      try {
        const data = JSON.parse(message.toString());

        switch (data.type) {
          case 'join-room':
            currentRoom = data.roomId;
            if (currentRoom && !rooms.has(currentRoom)) {
              rooms.set(currentRoom, new Set());
            }
            if (currentRoom) {
              rooms.get(currentRoom)!.add(ws);

              // Notify other participants
              rooms.get(currentRoom)!.forEach(client => {
                if (client !== ws && client.readyState === WebSocket.OPEN) {
                  client.send(JSON.stringify({ type: 'user-joined', userId: data.userId }));
                }
              });
            }
            break;

          case 'webrtc-offer':
          case 'webrtc-answer':
          case 'webrtc-ice-candidate':
            // Forward WebRTC signaling to other participants in room
            if (currentRoom && rooms.has(currentRoom)) {
              rooms.get(currentRoom)!.forEach(client => {
                if (client !== ws && client.readyState === WebSocket.OPEN) {
                  client.send(JSON.stringify(data));
                }
              });
            }
            break;
        }
      } catch (error) {
        console.error('WebSocket message error:', error);
      }
    });

    ws.on('close', () => {
      if (currentRoom && rooms.has(currentRoom)) {
        rooms.get(currentRoom)!.delete(ws);
        if (rooms.get(currentRoom)!.size === 0) {
          rooms.delete(currentRoom);
        }
      }
    });
  });

  // User routes
  app.post("/api/users", async (req, res) => {
    try {
      const userData = insertUserSchema.parse(req.body);
      const user = await storage.createUser(userData);
      res.json(user);
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.get("/api/users/firebase/:firebaseUid", async (req, res) => {
    try {
      const user = await storage.getUserByFirebaseUid(req.params.firebaseUid);
      if (!user) {
        return res.status(404).json({ error: "User not found" });
      }
      res.json(user);
    } catch (error) {
      res.status(500).json({ error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.put("/api/users/:id", async (req, res) => {
    try {
      const updates = insertUserSchema.partial().parse(req.body);
      const user = await storage.updateUser(req.params.id, updates);
      res.json(user);
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  // Doctor routes
  app.post("/api/doctors", async (req, res) => {
    try {
      const doctorData = insertDoctorSchema.parse(req.body);
      const doctor = await storage.createDoctor(doctorData);
      res.json(doctor);
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.get("/api/doctors/online", async (req, res) => {
    try {
      const doctors = await storage.getOnlineDoctors();
      res.json(doctors);
    } catch (error) {
      res.status(500).json({ error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.get("/api/doctors/specialty/:specialty", async (req, res) => {
    try {
      const doctors = await storage.getDoctorsBySpecialty(req.params.specialty);
      res.json(doctors);
    } catch (error) {
      res.status(500).json({ error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.put("/api/doctors/:id/status", async (req, res) => {
    try {
      const { isOnline } = req.body;
      const doctor = await storage.updateDoctor(req.params.id, { isOnline });
      res.json(doctor);
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  // Appointment routes
  app.post("/api/appointments", async (req, res) => {
    try {
      const appointmentData = insertAppointmentSchema.parse(req.body);
      const appointment = await storage.createAppointment(appointmentData);
      res.json(appointment);
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.get("/api/appointments/patient/:patientId", async (req, res) => {
    try {
      const appointments = await storage.getPatientAppointments(req.params.patientId);
      res.json(appointments);
    } catch (error) {
      res.status(500).json({ error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.get("/api/appointments/doctor/:doctorId", async (req, res) => {
    try {
      const appointments = await storage.getDoctorAppointments(req.params.doctorId);
      res.json(appointments);
    } catch (error) {
      res.status(500).json({ error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.put("/api/appointments/:id", async (req, res) => {
    try {
      const updates = insertAppointmentSchema.partial().parse(req.body);
      const appointment = await storage.updateAppointment(req.params.id, updates);
      res.json(appointment);
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  // Health records routes
  app.post("/api/health-records", async (req, res) => {
    try {
      const recordData = insertHealthRecordSchema.parse(req.body);
      const record = await storage.createHealthRecord(recordData);
      res.json(record);
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.get("/api/health-records/patient/:patientId", async (req, res) => {
    try {
      const records = await storage.getPatientHealthRecords(req.params.patientId);
      res.json(records);
    } catch (error) {
      res.status(500).json({ error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  // Medicine routes
  app.get("/api/medicines/search", async (req, res) => {
    try {
      const { q } = req.query;
      if (!q || typeof q !== 'string') {
        return res.status(400).json({ error: "Query parameter 'q' is required" });
      }
      const medicines = await storage.searchMedicines(q);
      res.json(medicines);
    } catch (error) {
      res.status(500).json({ error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.get("/api/medicines/:id/stock", async (req, res) => {
    try {
      const stock = await storage.getMedicineStock(req.params.id);
      res.json(stock);
    } catch (error) {
      res.status(500).json({ error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  // Pharmacy routes
  app.get("/api/pharmacies", async (req, res) => {
    try {
      const pharmacies = await storage.getPharmacies();
      res.json(pharmacies);
    } catch (error) {
      res.status(500).json({ error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  // Symptom checker routes
  app.post("/api/symptom-check", async (req, res) => {
    try {
      const { symptoms, patientId } = req.body;

      if (!symptoms || !Array.isArray(symptoms) || symptoms.length === 0) {
        return res.status(400).json({ error: "Symptoms array is required" });
      }

      if (!patientId) {
        return res.status(400).json({ error: "Patient ID is required" });
      }

      // AI analysis using EndlessMedical API (Free)
      const aiResponse = await analyzeWithEndlessMedical(symptoms);

      // Save symptom check to database
      const symptomCheck = await storage.createSymptomCheck({
        patientId,
        symptoms,
        aiResponse,
        severity: aiResponse.severity
      });

      res.json(symptomCheck);
    } catch (error) {
      console.error('Symptom check error:', error);
      res.status(500).json({ error: "Failed to analyze symptoms. Please try again later." });
    }
  });

  app.get("/api/symptom-checks/patient/:patientId", async (req, res) => {
    try {
      const checks = await storage.getPatientSymptomChecks(req.params.patientId);
      res.json(checks);
    } catch (error) {
      res.status(500).json({ error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  return httpServer;
}

server/storage.ts.content:
import { 
  users, doctors, appointments, healthRecords, medicines, pharmacies, medicineStock, symptomChecks,
  type User, type InsertUser, type Doctor, type InsertDoctor, type Appointment, type InsertAppointment,
  type HealthRecord, type InsertHealthRecord, type Medicine, type InsertMedicine,
  type Pharmacy, type InsertPharmacy, type MedicineStock, type InsertMedicineStock,
  type SymptomCheck, type InsertSymptomCheck
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, asc } from "drizzle-orm";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByFirebaseUid(firebaseUid: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, updates: Partial<InsertUser>): Promise<User>;

  // Doctor operations
  getDoctor(id: string): Promise<Doctor | undefined>;
  getDoctorByUserId(userId: string): Promise<Doctor | undefined>;
  createDoctor(doctor: InsertDoctor): Promise<Doctor>;
  updateDoctor(id: string, updates: Partial<InsertDoctor>): Promise<Doctor>;
  getOnlineDoctors(): Promise<(Doctor & { user: User })[]>;
  getDoctorsBySpecialty(specialty: string): Promise<(Doctor & { user: User })[]>;

  // Appointment operations
  createAppointment(appointment: InsertAppointment): Promise<Appointment>;
  getAppointment(id: string): Promise<Appointment | undefined>;
  getPatientAppointments(patientId: string): Promise<(Appointment & { doctor: Doctor & { user: User } })[]>;
  getDoctorAppointments(doctorId: string): Promise<(Appointment & { patient: User })[]>;
  updateAppointment(id: string, updates: Partial<InsertAppointment>): Promise<Appointment>;

  // Health record operations
  createHealthRecord(record: InsertHealthRecord): Promise<HealthRecord>;
  getPatientHealthRecords(patientId: string): Promise<HealthRecord[]>;
  getHealthRecord(id: string): Promise<HealthRecord | undefined>;

  // Medicine operations
  searchMedicines(query: string): Promise<Medicine[]>;
  getMedicine(id: string): Promise<Medicine | undefined>;
  createMedicine(medicine: InsertMedicine): Promise<Medicine>;

  // Pharmacy operations
  getPharmacies(): Promise<Pharmacy[]>;
  getPharmacy(id: string): Promise<Pharmacy | undefined>;
  createPharmacy(pharmacy: InsertPharmacy): Promise<Pharmacy>;

  // Medicine stock operations
  getMedicineStock(medicineId: string): Promise<(MedicineStock & { pharmacy: Pharmacy })[]>;
  updateMedicineStock(pharmacyId: string, medicineId: string, updates: Partial<InsertMedicineStock>): Promise<MedicineStock>;
  createMedicineStock(stock: InsertMedicineStock): Promise<MedicineStock>;

  // Symptom check operations
  createSymptomCheck(check: InsertSymptomCheck): Promise<SymptomCheck>;
  getPatientSymptomChecks(patientId: string): Promise<SymptomCheck[]>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByFirebaseUid(firebaseUid: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.firebaseUid, firebaseUid));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db.insert(users).values(insertUser).returning();
    return user;
  }

  async updateUser(id: string, updates: Partial<InsertUser>): Promise<User> {
    const [user] = await db.update(users).set(updates).where(eq(users.id, id)).returning();
    return user;
  }

  async getDoctor(id: string): Promise<Doctor | undefined> {
    const [doctor] = await db.select().from(doctors).where(eq(doctors.id, id));
    return doctor || undefined;
  }

  async getDoctorByUserId(userId: string): Promise<Doctor | undefined> {
    const [doctor] = await db.select().from(doctors).where(eq(doctors.userId, userId));
    return doctor || undefined;
  }

  async createDoctor(insertDoctor: InsertDoctor): Promise<Doctor> {
    const [doctor] = await db.insert(doctors).values(insertDoctor).returning();
    return doctor;
  }

  async updateDoctor(id: string, updates: Partial<InsertDoctor>): Promise<Doctor> {
    const [doctor] = await db.update(doctors).set(updates).where(eq(doctors.id, id)).returning();
    return doctor;
  }

  async getOnlineDoctors(): Promise<(Doctor & { user: User })[]> {
    const result = await db
      .select()
      .from(doctors)
      .innerJoin(users, eq(doctors.userId, users.id))
      .where(eq(doctors.isOnline, true))
      .orderBy(desc(doctors.rating));

    return result.map(({ doctors: doctor, users: user }) => ({ ...doctor, user }));
  }

  async getDoctorsBySpecialty(specialty: string): Promise<(Doctor & { user: User })[]> {
    const result = await db
      .select()
      .from(doctors)
      .innerJoin(users, eq(doctors.userId, users.id))
      .where(eq(doctors.specialty, specialty))
      .orderBy(desc(doctors.rating));

    return result.map(({ doctors: doctor, users: user }) => ({ ...doctor, user }));
  }

  async createAppointment(insertAppointment: InsertAppointment): Promise<Appointment> {
    const [appointment] = await db.insert(appointments).values(insertAppointment).returning();
    return appointment;
  }

  async getAppointment(id: string): Promise<Appointment | undefined> {
    const [appointment] = await db.select().from(appointments).where(eq(appointments.id, id));
    return appointment || undefined;
  }

  async getPatientAppointments(patientId: string): Promise<(Appointment & { doctor: Doctor & { user: User } })[]> {
    const result = await db
      .select()
      .from(appointments)
      .innerJoin(doctors, eq(appointments.doctorId, doctors.id))
      .innerJoin(users, eq(doctors.userId, users.id))
      .where(eq(appointments.patientId, patientId))
      .orderBy(desc(appointments.scheduledAt));

    return result.map(({ appointments: appointment, doctors: doctor, users: user }) => ({
      ...appointment,
      doctor: { ...doctor, user }
    }));
  }

  async getDoctorAppointments(doctorId: string): Promise<(Appointment & { patient: User })[]> {
    const result = await db
      .select()
      .from(appointments)
      .innerJoin(users, eq(appointments.patientId, users.id))
      .where(eq(appointments.doctorId, doctorId))
      .orderBy(desc(appointments.scheduledAt));

    return result.map(({ appointments: appointment, users: patient }) => ({
      ...appointment,
      patient
    }));
  }

  async updateAppointment(id: string, updates: Partial<InsertAppointment>): Promise<Appointment> {
    const [appointment] = await db.update(appointments).set(updates).where(eq(appointments.id, id)).returning();
    return appointment;
  }

  async createHealthRecord(insertRecord: InsertHealthRecord): Promise<HealthRecord> {
    const [record] = await db.insert(healthRecords).values(insertRecord).returning();
    return record;
  }

  async getPatientHealthRecords(patientId: string): Promise<HealthRecord[]> {
    return await db
      .select()
      .from(healthRecords)
      .where(eq(healthRecords.patientId, patientId))
      .orderBy(desc(healthRecords.createdAt));
  }

  async getHealthRecord(id: string): Promise<HealthRecord | undefined> {
    const [record] = await db.select().from(healthRecords).where(eq(healthRecords.id, id));
    return record || undefined;
  }

  async searchMedicines(query: string): Promise<Medicine[]> {
    return await db
      .select()
      .from(medicines)
      .where(eq(medicines.name, query))
      .orderBy(asc(medicines.name));
  }

  async getMedicine(id: string): Promise<Medicine | undefined> {
    const [medicine] = await db.select().from(medicines).where(eq(medicines.id, id));
    return medicine || undefined;
  }

  async createMedicine(insertMedicine: InsertMedicine): Promise<Medicine> {
    const [medicine] = await db.insert(medicines).values(insertMedicine).returning();
    return medicine;
  }

  async getPharmacies(): Promise<Pharmacy[]> {
    return await db.select().from(pharmacies).where(eq(pharmacies.isActive, true));
  }

  async getPharmacy(id: string): Promise<Pharmacy | undefined> {
    const [pharmacy] = await db.select().from(pharmacies).where(eq(pharmacies.id, id));
    return pharmacy || undefined;
  }

  async createPharmacy(insertPharmacy: InsertPharmacy): Promise<Pharmacy> {
    const [pharmacy] = await db.insert(pharmacies).values(insertPharmacy).returning();
    return pharmacy;
  }

  async getMedicineStock(medicineId: string): Promise<(MedicineStock & { pharmacy: Pharmacy })[]> {
    const result = await db
      .select()
      .from(medicineStock)
      .innerJoin(pharmacies, eq(medicineStock.pharmacyId, pharmacies.id))
      .where(and(eq(medicineStock.medicineId, medicineId), eq(pharmacies.isActive, true)))
      .orderBy(desc(medicineStock.quantity));

    return result.map(({ medicine_stock: stock, pharmacies: pharmacy }) => ({ ...stock, pharmacy }));
  }

  async updateMedicineStock(pharmacyId: string, medicineId: string, updates: Partial<InsertMedicineStock>): Promise<MedicineStock> {
    const [stock] = await db
      .update(medicineStock)
      .set({ ...updates, updatedAt: new Date() })
      .where(and(eq(medicineStock.pharmacyId, pharmacyId), eq(medicineStock.medicineId, medicineId)))
      .returning();
    return stock;
  }

  async createMedicineStock(insertStock: InsertMedicineStock): Promise<MedicineStock> {
    const [stock] = await db.insert(medicineStock).values(insertStock).returning();
    return stock;
  }

  async createSymptomCheck(insertCheck: InsertSymptomCheck): Promise<SymptomCheck> {
    const [check] = await db.insert(symptomChecks).values(insertCheck).returning();
    return check;
  }

  async getPatientSymptomChecks(patientId: string): Promise<SymptomCheck[]> {
    return await db
      .select()
      .from(symptomChecks)
      .where(eq(symptomChecks.patientId, patientId))
      .orderBy(desc(symptomChecks.createdAt));
  }
}

export const storage = new DatabaseStorage();

